    private void btnLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLoginActionPerformed
        
        String usuario = txtUsuario.getText();
        String contrasenia = txtContrasenia.getText();
        Usuario usr = control.validarUsuario(usuario,contrasenia);
        
        if(usr != null){
          String rol = usr.getUnRol().getNombreRol();//TRAIGO EL OBJ ROL CON EL NOMBRE DESDE LA CONTR LOGICA
             if(rol.equals("admin")){
                 PrincipalAdm padmin = new PrincipalAdm(control, usr);//SI ES ADMIN LO REGISTRO EN DICHA INTERFAZ
                 padmin.setVisible(true);
                 padmin.setLocationRelativeTo(null);
                 this.dispose();
               
             }
             if(rol.equals("user")){
               Principaluser puser = new Principaluser(control,usr);//
                 puser.setVisible(true);
                 puser.setLocationRelativeTo(null);
                  this.dispose();
             }
        }
        
        else{
            txtMensaje.setText("Usuario o contrase√±a incorrecto");;
        }
        
        
        
    }//GEN-LAST:event_btnLoginActionPerformed

 

